import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, FormBtn } from "../components/Form";

class Books extends Component {
  state = {
    books: [],
    searchRequest: "",
    title: "",
    author: "",
    synopsis: ""
  };

  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = () => {
    API.getBooks()
      .then(res =>
        this.setState({ books: res.data, title: "", author: "", synopsis: "" })
      )
      .catch(err => console.log(err));
  };

  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.findbook(this.state.searchRequest).then(results => {
      console.log(results.data);
      let newBooks = []
      results.data.items.forEach(element => {
        let book = {
          id : element.id,
          authors: element.volumeInfo.authors,
          description: element.volumeInfo.description,
          image: element.volumeInfo.imageLinks.thumbnail,
          link: element.volumeInfo.infoLink,
          title: element.volumeInfo.title
        };
        newBooks.push(book);
      });
      this.setState({books : newBooks})
      console.log(this.state.books)
    })
  };

  handleBookSave = event => {
    event.preventDefault();
    console.log (event.target)

  }



  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-6">
            <Jumbotron>
              <h1>What Books Should I Read?</h1>
            </Jumbotron>
            <form>
              <Input
                value={this.state.searchRequest}
                onChange={this.handleInputChange}
                name="searchRequest"
                placeholder="Search A Book"
              />
              <FormBtn
                disabled={!this.state.searchRequest}
                onClick={this.handleFormSubmit}
              >
                Find Books!
              </FormBtn>
              {this.state.books.length ? (
              <List>
                {this.state.books.map(book => (
                  <ListItem 
                  key={book.id}
                  id = {book.id}
                  title = {book.title}
                  author= {book.authors[0]}
                  description = {book.description}
                  link = {book.link}
                  image = {book.image}
                  >
                  <FormBtn
                    onClick = {this.handleBookSave}
                  > Save this book
                  </FormBtn>
                    {/* <Link to={"/books/" + book.id}>
                      <strong>
                        {book.title} by {book.authors[0]}
                      </strong>
                    </Link> */}
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
            </form>
          </Col>
          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>Books On My List</h1>
            </Jumbotron>
            {this.state.books.length ? (
              <List>
                {this.state.books.map(book => (
                  <ListItem key={book._id}>
                    <Link to={"/books/" + book._id}>
                      <strong>
                        {book.title} by {book.author}
                      </strong>
                    </Link>
                    <DeleteBtn onClick={() => this.deleteBook(book._id)} />
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Books;
